INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005194', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'AllesPiechocki2012', 'In this paper we first develop a framework for understanding how tagged data can be used to change the way in which decisions affecting governance are made. Ultimately data, however it is formatted, is simply a means towards an end and it provides no value added unless different decisions are made as a result of its availability. We use Elliott's (1998) model of decision making and apply it to the governance area to serve as a framework for an investigation of precisely how \{XBRL\} will provide value added. We then analyze the current specifications of XBRL, both its taxonomies and the way in which tagged data is rendered, to answer the question of whether XBRL—as opposed to the generic concept of tagged data—takes full advantage of its potential to improve governance decision making. ', NULL, NULL, 'Michael Alles and Maciej Piechocki', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2010.09.008', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'XBRL', NULL, NULL, NULL, 'XBRL: Research Implications and Future Directions ', '2', NULL, '91 - 108', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Will \{XBRL\} improve corporate governance?: A framework for enhancing governance decision making using interactive data☆ ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089510000746', '13', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'AllesPiechocki2012', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005191', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'AllesKoganVasarhelyi2008', 'Following the lead of recent papers by Demski [Demski J. Is Accounting an Academic Discipline? Account Horiz 2007;21(2): 153–157], Fellingham [Fellingham J. Is Accounting an Academic Discipline? Account Horiz 2007;21(2): 159–163] and Hopwood [Hopwood A. Whither Accounting Research? Account Rev 2007;82(5): 1365–1374] which questioned the direction and value added of non-AIS accounting research, we discuss the state of research in Accounting Information Systems. \{AIS\} researchers face a significant hurdle in undertaking value added research given that the financial and human resources that industry devotes to research and development of \{AIS\} technology dwarf the capabilities of academic researchers. In these circumstances, we put forward a paradigm for \{AIS\} research based on the principle of comparative advantage, which is the powerful economic force that ensures that trade can take place even between parties where one has an absolute superiority over the other. It is our contention that if \{AIS\} academics are to succeed in creating value added research then they have to identify what they can do that the \{AIS\} industry, despite all its financial and human resource advantages, cannot or will not do. And what economic theory indicates is that such opportunities to add value always exist — if only academics are willing to seek them out. We illustrate our paradigm by analyzing three potential sources of comparative advantage for \{AIS\} researchers and discussing illustrative examples of research in each of these areas. ', NULL, NULL, 'Michael G. Alles and Alexander Kogan and Miklos A. Vasarhelyi', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2008.06.001', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Theory', NULL, NULL, NULL, NULL, '4', NULL, '202 - 215', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Exploiting comparative advantage: A paradigm for value added research in accounting information systems ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089508000493', '9', '2008', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'AllesKoganVasarhelyi2008', NULL, '1');
