INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005092', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Alter2015', 'Vastly inconsistent definitions of the term “the IT artifact” in leading journals and conferences demonstrate why it no longer means anything in particular and should be retired from the active IS lexicon. Examples from the literature show why artifact-cousins, such as the IS artifact, sociotechnical artifact, social artifact, and ensemble artifact should be used with great care, if not retired as well. Any void created by these retirements could be filled through the following approaches: (i) relabeling with simple terms that are immediately understandable; (ii) adopting guidelines for making sense of the whole X-artifact family; and (iii) sidestepping the IT artifact and focusing directly on IT-enabled work systems in organizations.', NULL, NULL, 'Alter, Steven', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1111/isj.12048', NULL, NULL, NULL, NULL, NULL, NULL, 'Information Systems Journal', NULL, NULL, 'IT artifact, IS artifact, social artifact, technical artifact, ensemble artifact, sociotechnical artifact, rigour and relevance', NULL, NULL, NULL, NULL, '1', NULL, '47--60', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The concept of ‘IT artifact’ has outlived its usefulness and should be retired now', NULL, 'http://dx.doi.org/10.1111/isj.12048', '25', '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'Alter2015', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005097', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'AndresLorcaMartinez2009', 'This article aims at identifying the factors influencing the implementation of Web accessibility (WA) by European banks and its effects on the visibility through the Internet. We studied a database made up of 51 European banks whose shares are included in the Dow Jones EURO STOXX® TMI Banks [8300] Index. Regarding the factors for the implementation of WA, we considered two feasible reasons. First, WA adoption can be motivated by operational factors, as WA can contribute to increase the efficiency of the operations. Second, WA can also be understood as a part of the corporate social responsibility (CSR) strategy, so banks that are more committed with CSR should be more prone to implement WA. However, our results indicate that neither operational nor social factors seem to have exerted a significant influence on WA adoption. An implication of these findings is the advisability of orientating governmental policies to firm awareness of the fact that WA should be a part of the CSR activities of banks. Regarding the effects of the implementation of WA, our results indicate that the effort pays dividends in terms of Internet visibility. This could eventually contribute to increase future revenues and, therefore, the performance of WA-committed banks.', NULL, NULL, 'Andrés, Javier De and Lorca, Pedro and Martínez, Ana B.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1002/asi.21103', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of the American Society for Information Science and Technology', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '9', NULL, '1769--1780', NULL, NULL, NULL, 'Wiley Subscription Services, Inc., A Wiley Company', NULL, NULL, NULL, 'Economic and financial factors for the adoption and visibility effects of Web accessibility: The case of European banks', NULL, 'http://dx.doi.org/10.1002/asi.21103', '60', '2009', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'AndresLorcaMartinez2009', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005173', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ApostolouDormineyHassellEtAl2015', 'Abstract This review of the accounting education literature includes 256 articles published over the two-year period, 2013–2014, in six journals: (1) Journal of Accounting Education, (2) Accounting Education: An International Journal, (3) Advances in Accounting Education, (4) Global Perspectives on Accounting Education, (5) Issues in Accounting Education, and (6) The Accounting Educators' Journal. This article updates prior literature reviews by organizing and summarizing recent additions to the accounting education literature. These reviews are categorized into five sections corresponding to traditional lines of inquiry: (1) curriculum and instruction, (2) instruction by content area, (3) educational technology, (4) students, and (5) faculty. Suggestions for research in all areas are presented. Articles presenting instructional resources and cases published in the same six journals during 2013–2014 are listed in appendices categorized by the content area for which they are appropriate. ', NULL, NULL, 'Barbara Apostolou and Jack W. Dorminey and John M. Hassell and James E. Rebele', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NUL