INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005179', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'BaxterHunton2011', 'A growing body of literature spanning the social, psychological, and neurological sciences is studying the influence of affect (emotions, feelings and moods) within a variety of decision-making contexts. Accounting Information Systems (AIS) researchers interested in capturing affect within such contexts ought to consider methods that are designed to capture affect close to its realization. One such approach is the experience sampling method (ESM), developed as a way to collect individuals' subjective perceptions of their cognitions, motivations and affect as they become manifest. In short, \{ESM\} involves capturing brief responses from individuals throughout the day over the course of weeks or even months. By doing so, researchers can strengthen internal validity weaknesses associated with surveys and interviews, and increase ecological validity beyond laboratory experiments. \{ESM\} data is rich with possibilities for within- and between-participant comparisons. However, conducting an \{ESM\} study is much more intense and time-consuming than a one-time survey. In this paper, we discuss the concept of ESM, explain how and it can be used for studying affect, explore various theoretical considerations, and mention some key implementation issues. Our objective is to highlight the \{ESM\} and offer directions for \{AIS\} researchers interested in evaluating the suitability of the \{ESM\} method for their research programs. ', NULL, NULL, 'Ryan J. Baxter and James E. Hunton', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2010.12.002', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Experience sampling method', NULL, NULL, NULL, 'Special Issue on Methodologies in \{AIS\} Research ', '2', NULL, '90 - 98', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Capturing affect via the experience sampling method: Prospects for accounting information systems researchers ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089510000898', '12', '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'BaxterHunton2011', NULL, '1');
