INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005185', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'BharosaJanssenWijkEtAl2013', 'Transforming Business-to-Government (B2G) information exchange is a next frontier for reducing government spending while improving performance. This paper examines two different \{B2G\} information exchange architectures that reflect continuing transformations that empower some government agencies to do better compliance monitoring tasks with fewer resources. The win for the reporting companies is the lower cost of compliance. Instead of focusing on collecting compliance information from individual companies, the government agencies in this study focus on collecting information on the supply chain level, allowing for automated data reconciliation. Our findings reveal that pushing controls (automated checks) upstream (in company software and data sources) results in more efficiency, higher information quality and reduces redundant controls. The examined architectures exhibit high levels of compliance by design, meaning that many control objectives are by default encompassed in the design. This requires a well-aligned combination of data standardization (using shared syntax and semantics) and automated information processing (using an intelligent gateway between businesses and government agencies). However, achieving such an alignment is a difficult challenge; especially when taking into account that such transformations require solid governance, trust and high initial investments — prerequisites that are rare in many public-private partnerships. ', NULL, NULL, 'Nitesh Bharosa and Marijn Janssen and Remco van Wijk and Niels de Winne and Haiko van der Voort and Joris Hulstijn and Yao-hua Tan', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.giq.2012.08.006', NULL, NULL, NULL, NULL, NULL, NULL, 'Government Information Quarterly ', NULL, NULL, 'Business-to-government', NULL, NULL, NULL, '\{ICEGOV\} 2011 Supplement ', NULL, NULL, 'S9 - S18', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Tapping into existing information flows: The transformation to compliance by design in business-to-government information exchange ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0740624X12001475', '30, Supplement 1', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'BharosaJanssenWijkEtAl2013', NULL, '1');
