INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005188', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'BonsonBednarova2015', 'Abstract For most of the world's largest companies, reporting on non-financial information appears to be a continuing trend. Communication of social and environmental dimensions of the company plays a key role in the sustainable development of organizations, and therefore should be investigated more in depth. The aim of this empirical study is to analyse the extent to which Eurozone companies report on \{CSR\} indicators, according to the Integrated Scorecard Taxonomy Scoreboard of the Spanish Accounting and Business Association (AECA), and the factors that can influence its use. A content analysis was conducted on the annual sustainability reports on the websites of 306 Eurozone companies listed in the \{STOXX\} Europe 600. The results revealed an intensive use of corporate governance indicators, a moderate disclosure of environmental key performance indicators (KPIs), and a low use of social indicators. Our study also showed that there is an influence of sector, and the listing in \{DJSI\} on the extent of sustainability reporting. ', NULL, NULL, 'Enrique Bonsón and Michaela Bednárová', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.rcsar.2014.06.002', NULL, NULL, NULL, NULL, NULL, NULL, 'Revista de Contabilidad ', NULL, NULL, '\{CSR\} reporting', NULL, NULL, NULL, NULL, '2', NULL, '182 - 193', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '\{CSR\} reporting practices of Eurozone companies ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1138489114000284', '18', '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'BonsonBednarova2015', NULL, '1');
