INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005238', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'BrivotChoKuhn2015', 'Abstract This paper examines how the U.S. accounting profession, through the American Institute of Certified Public Accountants (AICPA), sought to restore its damaged reputation and re-legitimize its claim to self-regulation after the Enron scandal. We do so by analyzing the content of \{AICPA\} leaders’ web communications to members and outsiders of the Institute between 1997 and 2010 and draw upon the concepts of logics and discourse. We argue that the marketing language surrounding the AICPA's “Vision Project” prior to Enron (1997–2001) is not durably supplanted by the language of parrhesia, celebrated during the Enron crisis management episode (2002–2004) – it reemerges after 2005, juxtaposed to parrhesia. This study contributes to increasing our understanding of the institutional complexity of the accounting professional field by suggesting that this complexity is, in part, cultivated and reproduced by \{AICPA\} leaders’ navigation between different conceptions of being an accountant. Institutional complexity can thus be viewed as a resource, rather than a constraint, which provides flexible impression management opportunities. ', NULL, NULL, 'Marion Brivot and Charles H. Cho and John R. Kuhn', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.cpa.2015.04.001', NULL, NULL, NULL, NULL, NULL, NULL, 'Critical Perspectives on Accounting ', NULL, NULL, 'Accountability', NULL, NULL, NULL, NULL, NULL, NULL, '23 - 43', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Marketing or parrhesia: A longitudinal study of AICPA's shifting languages in times of turbulence ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1045235415000507', '31', '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'BrivotChoKuhn2015', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005203', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ButlerGhosh2015', 'Abstract Managers are influenced in their decisions by the information provided by managerial accountants. Two typical examples from textbooks are the irrelevance of sunk costs and, more recently, the affect of knowing the outcome of a decision or revised budget forecast. Individual differences in the cognitive ability of decision makers to use information can lead to systematic differences in judgments. We identify and label one of these individual cognitive differences comprehensive thinking ability: the ability to think about multiple paths, branches or alternatives. Significant comprehensive thinking ability is likely to mitigate systematic differences in judgment in many contexts. We report the results of a series of studies using a variation on the investment trap (sunk cost or irrelevant cost) problem and a probability revision task. The findings suggest that comprehensive thinking ability may also explain other common systematic differences in judgment. ', NULL, NULL, 'Stephen A. Butler and Dipankar Ghosh', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.bar.2014.09.002', NULL, NULL, NULL, NULL, NULL, NULL, 'The British Accounting Review ', NULL, NULL, 'Judgment', NULL, NULL, NULL, NULL, '1', NULL, '33 - 45', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Individual differences in managerial accounting judgments and decision making ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0890838914000699', '47', '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'ButlerGhosh2015', NULL, '1');
