INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005206', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Chen2012', 'This study examines the e-government implementation of eXtensible Business Reporting Language (XBRL) to increase accountability and transparency in business and financial information. The business and financial information gathered in \{XBRL\} format is machine-readable and interoperable, thereby improving the ease of public dissemination and analysis. This study focuses on identifying and examining the determinants of successful \{XBRL\} implementation and draws from several bodies of literature: e-government, institutionalism, collaborative public management, regulatory compliance, and management information systems to identify determinants of successful implementation. This study selects four diverse implementations: the Netherlands, Australia, the United States, and Singapore. Empirical analysis follows a comparative case study method. The findings of this exploratory study underscore the importance of program goals and strategic alignment in achieving information transparency and efficiency, the advantage of strategies correlating to institutional setting, the critical need to provide incentives for adoption, and the usefulness of incremental implementation. The managerial and theoretical implications of these findings as well as future research opportunities are explored. ', NULL, NULL, 'Yu-Che Chen', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.giq.2012.05.009', NULL, NULL, NULL, NULL, NULL, NULL, 'Government Information Quarterly ', NULL, NULL, 'e-Government', NULL, NULL, NULL, 'Social Media in Government - Selections from the 12th Annual International Conference on Digital Government Research (dg.o2011) ', '4', NULL, '553 - 563', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A comparative study of e-government \{XBRL\} implementations: The potential of improving information transparency and efficiency ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0740624X12001013', '29', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'Chen2012', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005209', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ChengLuSheu2009', 'Business intelligence (BI) applications within an enterprise range over enterprise reporting, cube and ad hoc query analysis, statistical analysis, data mining, and proactive report delivery and alerting. The most sophisticated applications of \{BI\} are statistical analysis and data mining, which involve mathematical and statistical treatment of data for correlation analysis, trend analysis, hypothesis testing, and predictive analysis. They are used by relatively small groups of users consisting of information analysts and power users, for whom data and analysis are their primary jobs. We present an ontology-based approach for \{BI\} applications, specifically in statistical analysis and data mining. We implemented our approach in financial knowledge management system (FKMS), which is able to do: (i) data extraction, transformation and loading, (ii) data cubes creation and retrieval, (iii) statistical analysis and data mining, (iv) experiment metadata management, (v) experiment retrieval for new problem solving. The resulting knowledge from each experiment defined as a knowledge set consisting of strings of data, model, parameters, and reports are stored, shared, disseminated, and thus helpful to support decision making. We finally illustrate the above claims with a process of applying data mining techniques to support corporate bonds classification. ', NULL, NULL, 'Hilary Cheng and Yi-Chuan Lu and Calvin Sheu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.eswa.2008.02.047', NULL, NULL, NULL, NULL, NULL, NULL, 'Expert Systems with Applications ', NULL, NULL, 'Business intelligence', NULL, NULL, NULL, NULL, '2, Part 2', NULL, '3614 - 3622', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'An ontology-based business intelligence application in a financial knowledge management system ', NULL, 'http://www.sciencedirect.com/science/article/pii/S095741740800119X', '36', '2009', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'ChengLuSheu2009', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00004960', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Cheng2009', 'The Securities and Exchange Commission (SEC) has been moving toward letting U.S. firms prepare financial statements using International Financial Reporting Standards (IFRSs), “converging” U.S. and international reporting standards. Even firms not otherwise involved in international trade may have to follow IFRSs. The author takes a close look at the SEC's proposed convergence road map, discusses the possible effects of IFRS convergence, pinpoints issues and concerns, and examines strategies to handle the changes ahead. © 2009 Wiley Periodicals, Inc.', NULL, NULL, 'Cheng, Kang', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1002/jcaf.20526', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of Corporate Accounting \& Finance', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '6', NULL, '31--37', NULL, NULL, NULL, 'Wiley Subscription Services, Inc., A Wiley Company', NULL, NULL, NULL, 'Navigating with the IFRS convergence roadmap', NULL, 'http://dx.doi.org/10.1002/jcaf.20526', '20', '2009', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'Cheng2009', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005211', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ChiuLiuVasarhelyi2014', 'Abstract The advances and continuous development of technology have been identified as significant influences on the accounting profession (AICPA, 1998). In the last twenty years, both academia and the accounting profession have been giving much attention to the demand and opportunity for audits to be performed automatically, continuously and in nearly real time. This paper presents a comprehensive review of continuous auditing research by providing an overview of the emergence and growth of the continuous auditing literature and classifying the extant continuous auditing research on the basis of four research characteristics indicated by a newly developed research taxonomy. ', NULL, NULL, 'Victoria Chiu and Qi Liu and Miklos A. Vasarhelyi', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.acclit.2014.08.001', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of Accounting Literature ', NULL, NULL, 'Continuous auditing', NULL, NULL, NULL, NULL, '1–2', NULL, '37 - 57', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The development and intellectual structure of continuous auditing research ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0737460714000032', '33', '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'ChiuLiuVasarhelyi2014', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005213', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ChoRoberts2010', 'This study uses Goffman's self-presentation theory to examine corporate website environmental disclosures from an organizational legitimacy perspective. We argue that corporations use Internet reporting and website platforms to project a more socially acceptable environmental management approach to public stakeholders. We argue further that this disclosure activity is often de-coupled from their actual environmental performance. To test these conjectures, we refine and employ a comprehensive disclosure evaluation metric to assess both the content and the presentation of these types of disclosures and utilize a firm's America's Toxic 100 toxic score, a newly developed measure based on the \{US\} Environmental Protection Agency's toxics release inventory (TRI) data, to proxy for environmental performance. Based on empirical tests of four size-matched samples, our findings support our conjectures, showing that worse environmental performers provide more extensive disclosure in terms of content and website presentation. ', NULL, NULL, 'Charles H. Cho and Robin W. Roberts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2009.12.003', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Internet reporting', NULL, NULL, NULL, NULL, '1', NULL, '1 - 16', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Environmental reporting on the internet by America's Toxic 100: Legitimacy and self-presentation ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089509000633', '11', '2010', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'ChoRoberts2010', NULL, '1');
