INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005285', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'DebrecenyFarewellPiechockiEtAl2010', 'In 2009, the Securities and Exchange Commission rolled out the first stage of a multi-year program to transition its \{EDGAR\} disclosure repository to the \{XBRL\} format. The quality of the \{XBRL\} data in the repository is vital for the success of the Commission’s interactive data program. A key aspect of the data quality of these filings is the correctness of the mathematical relationships implied by the taxonomy and implemented in the instance document. One quarter of the filings by the initial 400 large corporations in the first round of submissions had errors, with differences reported monetary facts and the sum of other monetary facts that were bound together in a computation relationship. The primary cause of these errors was inappropriate treatment in the instance documents of underlying debit/credit assumptions in the taxonomy. The results of the research have a number of implications for filers, the SEC, \{XBRL\} US, software vendors and the global \{XBRL\} community. ', NULL, NULL, 'Roger Debreceny and Stephanie Farewell and Maciej Piechocki and Carsten Felden and André Gräning', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.jaccpubpol.2010.04.001', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of Accounting and Public Policy ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '3', NULL, '296 - 306', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Does it add up? Early evidence on the data quality of \{XBRL\} filings to the \{SEC\} ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0278425410000219', '29', '2010', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'DebrecenyFarewellPiechockiEtAl2010', NULL, '1');
