INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005024', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'GhaniLaswadTooley2011', 'This study examines whether, in the presentation of financial information, digital formats address the concern over users’ functional fixation. The accounting literature indicates that the presentation of financial information either within the financial statements or in the notes to the financial statements often creates functional fixation where users of financial statements fail to adjust for differences in accounting policy. This leads users to judge what would otherwise be identical financial situations as being different due to the different accounting policies and methods adopted. It has been suggested that the use of digital formats in presenting financial reports may overcome functional fixation. Using an experimental design involving accountants in public practice, the results indicate that the use of digital formats to present financial reports does not fully overcome the issue of functional fixation in the processing of financial information. Although the participants were able to identify and extract relevant information, irrespective of whether or not the information was presented within the financial statements or in the notes to the accounts, the evidence indicates that functional fixation remained when the participants made final decisions based on available information. This suggests that functional fixation may not be caused by access to or extraction of information but by the level of perceived significance based on where the information is reported in the financial statements. In general, the results indicate that current technology may not be able to fully reduce functional fixation in the evaluation of financial information prepared in accordance with different accounting policies and methods. ', NULL, NULL, 'Erlane K. Ghani and Fawzi Laswad and Stuart Tooley', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.bar.2011.06.004', NULL, NULL, NULL, NULL, NULL, NULL, 'The British Accounting Review ', NULL, NULL, 'Recognition', NULL, NULL, NULL, NULL, '3', NULL, '186 - 199', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Functional fixation: Experimental evidence on the presentation of financial information through different digital formats ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0890838911000515', '43', '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'GhaniLaswadTooley2011', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005036', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'GonzalezSharmaGalletta2012', 'The concept of continuous auditing originated over two decades ago. Yet despite its much touted benefits, its acceptance and use in practice has been slow. To gain insight into the state of affairs, we surveyed 210 internal auditors worldwide on the status of their use of continuous auditing. Using the Unified Theory of Acceptance and Use of Technology (UTAUT) we explore the antecedents of internal auditors' intentions to use continuous auditing technology. Employing the Partial Least Squares method, we find strong support for the model with an \{R2\} of 44.3%. Specifically, we find that internal auditors' perceptions of effort expectancy and social influence are significant predictors of their intentions to use continuous auditing. We also find that annual sales volume of the company and voluntariness of use significantly moderate the relationship between performance expectancy and social influence respectively. Additionally, we find regional differences in the significance of key \{UTAUT\} antecedents. Specifically, we find that the North American internal auditors are more likely to use continuous auditing due to soft social coercion pressures of Social Influence through peers and higher authorities. On the other hand, Middle Eastern auditors are more likely to use the technology if it is mandated by the higher authorities. ', NULL, NULL, 'George C. Gonzalez and Pratyush N. Sharma and Dennis F. Galletta', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2012.06.009', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Continuous auditing', NULL, NULL, NULL, '2011 Research Symposium on Information Integrity \&amp; Information Systems Assurance ', '3', NULL, '248 - 262', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The antecedents of the use of continuous auditing in the internal auditing context ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089512000401', '13', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'GonzalezSharmaGalletta2012', NULL, '1');
