INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005105', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'HavelkaMerhout2013', 'Abstract A theoretical framework of the internal information technology audit (ITA) process is developed by collecting data from multiple sources using structured group processes. A series of nominal group processes involving information technology auditors, \{ITA\} managers, and financial auditors from three different internal audit organizations was performed to generate source data. This data was then coded and analyzed using a constant comparison approach to identify the codes, indicators, concepts, and relationships included in the theory. The results reveal 26 concepts organized into six categories: Audit Organization, Client Organization, Enterprise Environment, Process and Methodology, Target Process or System, and Audit Personnel. Based on the data and the concepts identified ten propositions are suggested. The results are then compared with prior research. ', NULL, NULL, 'Douglas Havelka and Jeffrey W. Merhout', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2012.12.001', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Information technology audit', NULL, NULL, NULL, NULL, '3', NULL, '165 - 192', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Internal information technology audit process quality: Theory development using structured group processes ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089512000760', '14', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'HavelkaMerhout2013', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005271', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'HeidariFeldenKoschtial2013', 'There is a consensus (Hoffman and Strand, 2001; Hannon, 2002; Bovee et al., 2005; Willis, 2005; Cox, 2006) that XBRL (Extensible Business Reporting Language) as a technical standard for facilitating transfer and analysis of financial statements could improve the speed and quality of transmitting, analyzing, and even more accurate financial reports by providing machine readable documents. Footnotes, which include important explanations about financial values, have still an unstructured format and are an obstacle for analysts and other stakeholders who want to benefit from analyzing financial statements. It is of interest how data integration approaches can support and facilitate the process of data extraction from footnotes automatically to gain accurate and reasonable analysis to avoid manual tasks. To address this issue, a state of the art is needed to identify and cluster relevant existing methods in terms of structured and unstructured data integration. It is shown that most of the existing literature is focused on a storage level of data integration. Other researchers deal with methods and tools to integrate and analyze structured and unstructured data separately. But, no identified paper illustrates an unstructured data integration solution to support analytical tasks based on XBRL documents. &copy; (2013) by the AIS/ICIS Administrative Office All rights reserved.', 'Chicago, IL, United states', NULL, 'Heidari, Maryam and Felden, Carsten and Koschtial, Claudia', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '19th Americas Conference on Information Systems, AMCIS 2013 - Hyperconnected World: Anything, Anywhere, Anytime', NULL, 'Data integration', 'Administrative data processing;Digital storage;Finance;Information systems;Quality control;', 'English', NULL, NULL, 'Financial analysis;Financial statements;Footnotes;Integration approach;State of the art;Technical standards;Unstructured data;XBRL;', NULL, NULL, '2108 - 2116', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Integration of structured and unstructured data in the financial analysis domain - A state of the art', NULL, NULL, '3', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'HeidariFeldenKoschtial2013', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005114', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'HendersonSheetzTrinkle2012', 'Prior research has investigated organizational adoption of inter-organizational Information Technology (IT) using the Technological–Organizational–Environmental (TOE) framework. While that research has yielded important insights into the adoption of inter-organizational IT, new technologies, such as XBRL, can be used inter-organizationally or internally. As such, testing the \{TOE\} framework in both an internal and inter-organizational context is vital because the independent variables explaining internal adoption may be different from those explaining inter-organizational adoption. Using \{XBRL\} as a representative example, the \{TOE\} framework is used in this study to investigate \{XBRL\} adoption for both internal and inter-organizational purposes. The results suggest that the drivers of internal adoption differ from those of inter-organizational adoption. Based on these results, we encourage future research to consider whether the significance of technological, organizational, and environmental variables change based on whether a technology is adopted internally or inter-organizationally. From a practical perspective, the findings can help professional associations encourage adoption of XBRL. ', NULL, NULL, 'Dave Henderson and Steven D. Sheetz and Brad S. Trinkle', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2012.02.001', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'XBRL', NULL, NULL, NULL, 'XBRL: Research Implications and Future Directions ', '2', NULL, '109 - 140', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The determinants of inter-organizational and internal in-house adoption of XBRL: A structural equation model ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089512000139', '13', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'HendersonSheetzTrinkle2012', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005123', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'HeraviLycettCesare2014', 'Abstract Business-to-Business (B2B) interoperations are an important part of today's global economy. Business process standards are developed to provide a common understanding of the information shared between trading partners. These standards, however, mainly capture the syntax of the transactions and not their semantics. This paper proposes the use of ontologies as the basis for standards development and presents an ontology for the ebXML Business Process Specification Schema (ebBP) with the aim of empowering the capture and sharing of semantics embedded within \{B2B\} processes as well as enabling knowledge deduction and reasoning over the shared knowledge. The paper utilises the Ontology-based Standards Development methodology (OntoStanD) as a methodological approach for designing ontological models of standards. This research demonstrates how Semantic Web technologies can be utilised as a basis for standards development and representation in order to improve standards-based interoperability between trading partners. ', NULL, NULL, 'Bahareh Rahmanzadeh Heravi and Mark Lycett and Sergio de Cesare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2014.01.005', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Ontology-based Standards', NULL, NULL, NULL, 'Business Process Modeling ', '3', NULL, '275 - 297', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ontology-based standards development: Application of OntoStanD to ebXML business process specification schema ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089514000062', '15', '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'HeraviLycettCesare2014', NULL, '1');
