INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005129', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'HoltzblattTschakert2011', 'In the past, many activities of accounting professors were restricted or confined by their classroom walls. Bringing in guest speakers, taking students on field trips and teaching students off-campus (either locally or globally) were extraordinary efforts that required significant organization. Today, numerous technological advancements in communication infrastructure, equipment and online tools greatly facilitate such initiatives. Outside experts can now visit the classroom, students can explore or collaborate in distant places and professors can extend the geographical reach of their lessons simply via the means of digital video technology. Based on our 2010 Conference on Teaching and Learning in Accounting (CTLA) Master Class, we share our experiences in exploring the use of digital video in teaching accounting and explain how numerous accounting professors are taking advantage of the capabilities afforded by digital video technologies. Online video clips, student video projects, and online video lecture recordings hold great promise for accounting education. We have created a website that complements and demonstrates the teaching ideas presented in this article and that facilitates video integration into accounting courses. We discuss the pedagogical benefits of using video, including those from general education and accounting literature. The article concludes with suggestions for how accounting faculty can keep current with video technology, areas for future accounting research and a call to action for accounting educators. Our work with digital video technology has led to the 2011 American Accounting Association (AAA) Innovation in Accounting Education Award as well as the 2010 Canadian Academic Accounting Association (CAAA) Howard Teall Innovation in Accounting Education Award. ', NULL, NULL, 'Mark Holtzblatt and Norbert Tschakert', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.jaccedu.2011.10.003', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of Accounting Education ', NULL, NULL, 'Teaching with technology', NULL, NULL, NULL, NULL, '2–3', NULL, '100 - 121', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Expanding your accounting classroom with digital video technology ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0748575111000686', '29', '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'HoltzblattTschakert2011', NULL, '1');
