INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005078', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'JansAllesVasarhelyi2013', 'Process mining aims to extract knowledge from the event logs maintained by a company's \{ERP\} system. The objective of this paper is to make the case for why internal and external auditors should leverage the capabilities process mining offers to rethink how auditing is carried out. We do so by identifying the sources of value added of process mining when applied to auditing, which are as follows: 1. process mining analyzes the entire population of data and not just a sample; 2. critically that data consists of meta-data—data entered independently of the actions of auditee—and not just data entered by the auditee; 3. process mining allows the auditor to have a more effective way of implementing the audit risk model by providing effective ways of conducting the required walkthroughs of processes and conducting analytic procedures; 4. process mining allows the auditor to conduct analyses not possible with existing audit tools, such as discovering the ways in which business processes are actually being carried out in practice, and to identify social relationships between individuals. It is our argument that these sources of value have not been fully understood in the process mining literature, which has focused on developing it as a statistical methodology rather than on applying it to audit practice. Only when auditors and audit researchers appreciate what is new and unique about process mining will its acceptance in auditing practice become feasible. ', NULL, NULL, 'Mieke Jans and Michael Alles and Miklos Vasarhelyi', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2012.06.015', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Event logs', NULL, NULL, NULL, NULL, '1', NULL, '1 - 20', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The case for process mining in auditing: Sources of value added and areas of application ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089512000462', '14', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'JansAllesVasarhelyi2013', NULL, '1');
