INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005081', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'JanssenEstevez2013', 'Abstract Governments from all over the world are looking for ways to reduce costs while at the same time to stimulate innovation. While pursuing both objectives, governments face a major challenge—to operate in a connected environment, engage stakeholders and solve societal problems by utilizing new methods, tools, practices and governance models. As result, fundamental changes are taking place on how government operates. Such changes are under the larger umbrella of ‘lean government’ (l-Government). Lean government is a new wave which is appearing as a response to traditional approaches—like electronic government (e-Government) and transformational government (t-Government), and aims at reducing the complexity of the public sector by simplifying and streamlining organizational structures and processes, at the same time at stimulating innovation by mobilizing stakeholders. In l-Government, public organizations introduce platforms facilitating innovation and interactions with other public organizations, business and citizens, and focus on their orchestration role. Experimentation, assessment and gradual improvement based on user requirements are key factors for realizing l-Government. ', NULL, NULL, 'Marijn Janssen and Elsa Estevez', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.giq.2012.11.003', NULL, NULL, NULL, NULL, NULL, NULL, 'Government Information Quarterly ', NULL, NULL, 'e-Government', NULL, NULL, NULL, '\{ICEGOV\} 2011 Supplement ', NULL, NULL, 'S1 - S8', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Lean government and platform-based governance—Doing more with less ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0740624X12001517', '30, Supplement 1', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'JanssenEstevez2013', NULL, '1');
