INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005226', (SELECT entry_types_id FROM entry_types WHERE label='incollection'), 'Khanna2008', 'Publisher Summary Industry standards are created after extensive discussion between experienced industry players and are helpful in reducing errors, managing risk, and increasing efficiency and productivity. If they are published free of license fees, they are known as open standards. For a straight through processing (STP) environment, three types of standards are relevant that include business standards, technology standards, and financial ontologies. This chapter focuses on financial technology standards relevant to creating an \{STP\} environment. It introduces financial technology protocols and discusses the value of using industry standards for electronic communication of trade data. Currently, three industry standards, FIX, FpML, and SWIFT, are used for system communication of financial data in the securities industry. Firms build their own communication vocabularies for exchanging financial data, and in order to use industry standards, they must build translators that can convert the industry format and vocabulary to their legacy ones. An insight is provided into how firms contribute to, transform, and extend industry standards to meet their own needs. The move toward industry standards is crucial for achieving \{STP\} in the trade lifecycle, and more and more firms are adopting one of these ontologies for interfirm communication. However, concern remains over which format to use for high-speed high-volume trade and market data, and firms struggle to translate industry standards to their internal legacy data formats. ', 'Burlington', NULL, 'Ayesha Khanna', 'Straight Through Processing for Financial Services ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/B978-012466470-8.50006-2', NULL, 'Khanna, Ayesha ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '69 - 89', NULL, NULL, NULL, 'Academic Press', NULL, NULL, 'Complete Technology Guides for Financial Services', 'Chapter 4 - Industry Standards ', NULL, 'http://www.sciencedirect.com/science/article/pii/B9780124664708500062', NULL, '2008', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'Khanna2008', NULL, '1');
