INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005297', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'KotbRoberts2011', 'This study seeks to further understand the implications of e-business for financial audit practice by exploring: the pressures leading to the adoption of new or different audit practices; the specific features of e-business that affect the appropriateness of traditional auditing practices and techniques; and how technical audit work is changing in response to the increasing adoption of e-business models. By using semi-structured interviews and a postal questionnaire survey, the perceptions of financial auditors and information technology (IT) audit practitioners were sought. The findings identify factors affecting, and pressures leading to, audit change in response to e-business as perceived by these auditors. Overall, the most obvious aspect of e-business audit change was felt to be the extensive use of IT-driven practices and techniques, and this change could not only be attributed to technical factors, but they also thought that competitive and professional factors were also important drivers of audit change.', NULL, NULL, 'Kotb, Amr and Roberts, Clare', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1111/j.1099-1123.2011.00427.x', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Auditing', NULL, NULL, 'e-business, e-commerce, e-business audit, IT audit, audit practice, audit change', NULL, NULL, NULL, NULL, '2', NULL, '150--175', NULL, NULL, NULL, 'Blackwell Publishing Ltd', NULL, NULL, NULL, 'The Impact of E-Business on the Audit Process: An Investigation of the Factors Leading to Change', NULL, 'http://dx.doi.org/10.1111/j.1099-1123.2011.00427.x', '15', '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'KotbRoberts2011', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005255', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'KotbRobertsSian2012', 'This study sets out to examine the impact of technological change on the external audit function of e-businesses and, specifically, the professionals involved in executing it. Utilising semi-structured interviews combined with a questionnaire survey, this paper explores the possible implications of developments in e-business audit for financial auditors as a professional group. The findings suggest that the traditional authority enjoyed by external financial auditors is being, and will be, increasingly challenged by \{IT\} audit specialists. The role of the professional bodies, responsible for the education and training of financial audit professionals, in particular, is highlighted as key to the outcome if they are to fend off challenges in this growing arena and retain jurisdictional control. ', NULL, NULL, 'Amr Kotb and Clare Roberts and S. Sian', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.cpa.2012.03.003', NULL, NULL, NULL, NULL, NULL, NULL, 'Critical Perspectives on Accounting ', NULL, NULL, 'Accountability', NULL, NULL, NULL, NULL, '6', NULL, '468 - 482', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'E-business audit: Advisory jurisdiction or occupational invasion? ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1045235412000664', '23', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'KotbRobertsSian2012', NULL, '1');
