INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005276', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Kunzler2002', 'We are living in a fast changing world, especially in the ICT (Information and Communication Technologies) sector. In this difficult environment people like to use buzzwords and acronyms - in the title of this paper you find some of them used a lot in the statistical context. It is no problem to add more: CASI, CSAQ, EDI, XML, XBRL - all of them and many more will appear in this article (and in the glossary). It is not always easy for statisticians and ICT specialists to understand each other. However, ICT is needed to automate statistical data reporting in order to save resources and improve processes - so both communities are condemned to co-operate. In this scenario, a third area with yet another jargon is playing an important role, namely e-business: more and more, statistical EDR is using e-business tools and methods, like for example EDIFACT or ebXML. This paper tries to shed some light on the ICT and e-business aspects of statistical data reporting. The questions addressed in this paper include: What is EDR? What is metadata for EDR? Which metadata standards for EDR are available or coming up? And how is the ESS concerned or involved? The current situation of EDI standardisation is examined in more detail: the transition from EDIFACT to XML and the adoption of ebXML for the ESS.', NULL, NULL, 'Kunzler, Uwe', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Statistical Journal of the United Nations Economic Commission for Europe', NULL, 'Statistical methods', 'Electronic commerce;Information technology;Metadata;XML;', 'English', NULL, NULL, 'Electronic data reporting (EDR);', '3', NULL, '119 - 130', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Electronic data reporting (EDR), metadata, standards and the European statistical system (ESS)', NULL, NULL, '19', '2002', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'Kunzler2002', NULL, '1');
