INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005268', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'LightstoneDriscoll2008', 'This paper focuses on some of the ethical issues related to voluntary disclosure of qualitative information by Canadian public companies. Drawing on various organizational theories, we examine some of the ways that companies can symbolically manage legitimacy through disclosure. Press releases of a sample of companies that received cease-trading orders were analysed for their use of language. We found that high-risk companies attempted to manage legitimacy by selectively releasing information and by using ambiguous language. Moreover, some companies behaved unethically by using language that suggested a positive future despite the imminent release of a cease-trading order. These findings have implications for organizational and accounting theorists and stakeholders in the corporate, fiduciary, and investment communities. Copyright © 2008 ASAC. Published by John Wiley & Sons, Ltd.', NULL, NULL, 'Lightstone, Karen and Driscoll, Cathy', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1002/cjas.50', NULL, NULL, NULL, NULL, NULL, NULL, 'Canadian Journal of Administrative Sciences / Revue Canadienne des Sciences de l'Administration', NULL, NULL, 'JEL classifications: M14, M49, voluntary disclosure, investor welfare, symbolic legitimacy management, company ethics', NULL, NULL, NULL, NULL, '1', NULL, '7--21', NULL, NULL, NULL, 'John Wiley \& Sons, Ltd.', NULL, NULL, NULL, 'Disclosing elements of disclosure: a test of legitimacy theory and company ethics', NULL, 'http://dx.doi.org/10.1002/cjas.50', '25', '2008', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'LightstoneDriscoll2008', NULL, '1');
