INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005180', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'LiuWangYao2014', 'Abstract This paper investigates the benefits of mandatory adoption of the eXtensible Business Reporting Language (XBRL) in the U.S. Using a sample of Phase I and Phase \{II\} filers in year 2009 and 2010, we examine whether there exists a positive association between the number of analysts following a firm as well as analyst forecast accuracy and the \{XBRL\} mandate by the Securities and Exchange Commission (SEC). Our empirical results demonstrate a significant positive association between mandatory \{XBRL\} adoption and both analyst following and forecast accuracy. In addition, our findings show that such an association is stronger for Phase I filers than for Phase \{II\} filers in 2010. The magnitude of the association between \{XBRL\} mandate and analyst following is also larger for Phase I filers in year two than in year one of adoption. Our findings not only support the SEC’s requirement of detailed tagging of footnotes but also show that the benefits of adopting \{XBRL\} are realized regardless of errors found and concerns raised at the early stage of adoption. ', NULL, NULL, 'Chunhui Liu and Tawei Wang and Lee J. Yao', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.jaccpubpol.2013.10.004', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of Accounting and Public Policy ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', NULL, '69 - 82', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'XBRL’s impact on analyst forecast behavior: An empirical study ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0278425413000914', '33', '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'LiuWangYao2014', NULL, '1');
