INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005277', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'LorcaAndreesMartinez2012', 'Web accessibility (WA) is an innovation in Web design; it can be considered as part of the corporate social responsibility (CSR) strategy of the firms. As adoption of innovations and CSR commitment are linked with firm size and national culture/legislation, we hypothesize that size and national culture/legislation, may have an effect on WA level. The authors studied an international sample made up of companies included in EUROSTOXX600 (The STOXX Europe 600 Index). The main results suggest that both size and culture have a significant effect on WA. Large firms as well as Anglo-Saxon companies are more prone to have higher WA levels. A deeper analysis, which was done through the estimation of quantile regression equations, showed that the influence of size is significant for companies trying to excel or for those trying to avoid the worst WA. However, the effect of size is significant only in the lowest part of the conditional distribution.', NULL, NULL, 'Lorca, Pedro and Andrées, Javier De and Martínez, Ana B.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1002/asi.21650', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of the American Society for Information Science and Technology', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', NULL, '392--405', NULL, NULL, NULL, 'Wiley Subscription Services, Inc., A Wiley Company', NULL, NULL, NULL, 'Size and culture as determinants of the web policy of listed firms: The case of web accessibility in Western European countries', NULL, 'http://dx.doi.org/10.1002/asi.21650', '63', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'LorcaAndreesMartinez2012', NULL, '1');
