INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00004984', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Lupiani-RuizGarcia-ManotasValencia-GarciaEtAl2011', 'An increasingly large amount of financial information available in a number of heterogeneous business sources implies that the traditional methods of analysis are no longer applicable. These financial data sources are characterized by the use of disparate data models and their unstructured content with implicit knowledge. In addition, the most up-to-date financial information typically resides in the vast amount of financial-related news that brokers take into account when investing. As Semantic Technologies mature, they provide a consistent and reliable basis for the development of superior, more precise mechanisms to deal with heterogeneous data. In this paper, we present a financial news semantic search engine based on Semantic Web technologies. The search engine is accompanied by an ontology population tool that assists in keeping the financial ontology up-to-date. In addition, a further module has been developed that is capable of crawling the Web in search of financial news and annotating it with knowledge entities from the financial ontology that match with the contents of the news. Our contribution is an overall solution based on a fully fledged architecture that has been validated in a use case scenario for the Spanish stock exchange. ', NULL, NULL, 'Eduardo Lupiani-Ruiz and Ignacio García-Manotas and Rafael Valencia-García and Francisco García-Sánchez and Dagoberto Castellanos-Nieves and Jesualdo Tomás Fernández-Breis and Juan Bosco Camón-Herrero', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.eswa.2011.06.003', NULL, NULL, NULL, NULL, NULL, NULL, 'Expert Systems with Applications ', NULL, NULL, 'Semantic search engine', NULL, NULL, NULL, NULL, '12', NULL, '15565 - 15572', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Financial news semantic search engine ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0957417411008980', '38', '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'Lupiani-RuizGarcia-ManotasValencia-GarciaEtAl2011', NULL, '1');
