INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005286', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'MarkelevichShawWeihs2015', 'eXtensible Business Reporting Language (XBRL) is a language for the electronic communication of business and financial data which is revolutionizing business reporting around the world. It is a tool to bridge potential language barriers and unify financial reporting. This has appeal to foreign investors, among others, who can rely on information in XBRL-tagged financial reports to make investment decisions without having to translate financial statements from local language. In 2008, Israel required most public companies to adopt International Financial Reporting Standards (IFRS) for financial reporting and to use XBRL-tagged reporting format, as part of an aggressive effort to make its capital markets more transparent and attractive for foreign investors. In this paper, we study all Israeli public companies and analyze the accuracy and reliability of their XBRL-tagged financial statements that are available on MAGNA, the Israel Securities Authority's electronic system. We describe the process by which the XBRL-based data were collected and reported. We document, categorize, and analyze deficiencies in the XBRL-tagged filings, and inconsistencies between them and the Hebrew-based annual reports. We observe pervasive data entry errors resulting in inaccurate XBRL-generated financial reports, which went undetected for over one year. Further, first year XBRL reporting (in conjunction with IFRS adoption) did not increase foreign investment in the Israeli capital markets. This analysis allows us to better understand the benefits and challenges of the adoption of XBRL.', NULL, NULL, 'Markelevich, Ariel and Shaw, Lewis and Weihs, Hagit', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1111/1911-3838.12044', NULL, NULL, NULL, NULL, NULL, NULL, 'Accounting Perspectives', NULL, NULL, 'XBRL, IFRS, Data error, Data transformation', NULL, NULL, NULL, NULL, '2', NULL, '117--133', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The Israeli XBRL Adoption Experience', NULL, 'http://dx.doi.org/10.1111/1911-3838.12044', '14', '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'MarkelevichShawWeihs2015', NULL, '1');
