INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00004983', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'MenaLopezFraminanEtAl2010', 'In this paper we discuss the results of a research project called eXtensible Project Documentation Reporting Language (XPDRL). The main objective of this project is to improve the quality of the core project documentation that serves for supporting the construction works in Spain and to facilitate an efficient exchange of information between the stakeholders in the Spanish Architectural Engineering and Construction sector, in particular their relationship with the Professional Associations and Spanish control public bodies. Starting from the premise that high quality project documentation is the basic precondition for a good quality in all the phases of the whole buildings and facilities life cycle, the project is based on Internet, \{XBRL\} (eXtensible Business Reporting Language) and the new Spanish standards about the quality in the project documentation (UNE 157000 series). As a result, a new set of \{XBRL\} taxonomies has been developed to support the processes of elaboration, verification, sending to the Professional Association, digital stamping, delivery, control, compulsory registration and storage of the construction project core documentation by the public authorities. Our proposal can be extended to other countries, particularly in Europe, since regulations are becoming similar in all \{EU\} countries. The potential for spin-off technology utilization is also significant in the areas of insurance, inspection chamber, digital reporting and project management. ', NULL, NULL, 'Ángel Mena and Fernando López and José Manuel Framiñan and Francisco Flores and Juan Manuel Gallego', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.autcon.2009.10.001', NULL, NULL, NULL, NULL, NULL, NULL, 'Automation in Construction ', NULL, NULL, 'Projects documentation quality', NULL, NULL, NULL, NULL, '2', NULL, '270 - 282', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '\{XPDRL\} project: Improving the project documentation quality in the Spanish architectural, engineering and construction sector ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0926580509001526', '19', '2010', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'MenaLopezFraminanEtAl2010', NULL, '1');
