INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005305', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'NakayamaObaAramotoEtAl2008', 'Message layer security is necessary to ensure the end-to-end security of Web services. To provide confidentiality against the intermediaries along the message path, XML encryption is used to partially encrypt the message. Because the data structure is changed by the partial encryption, the encrypted message is no longer valid with respect to the original schema definition. Thus, problems occur regarding the processing of the schema validation and the data binding by the intermediary. In this paper, we discuss two possible methods to solve these problems. The first method is to transform the original schema definition. The second is to transform the received message. We examined these methods by applying them to demonstration experiment of Web services. © 2008 Wiley Periodicals, Inc. Electron Comm Jpn, 91(5): 26– 32, 2008; Published online in Wiley InterScience (www.interscience.wiley.com). DOI 10.1002/ecj.10112', NULL, NULL, 'Nakayama, Kojiro and Oba, Michiko and Aramoto, Michitaka and Komoda, Norihisa', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1002/ecj.10112', NULL, NULL, NULL, NULL, NULL, NULL, 'Electronics and Communications in Japan', NULL, NULL, 'XML, Web services, security, XML encryption, schema validation, data binding.', NULL, NULL, NULL, NULL, '5', NULL, '26--32', NULL, NULL, NULL, 'Wiley Subscription Services, Inc., A Wiley Company', NULL, NULL, NULL, 'Processing methods for partially encrypted data in multihop Web services', NULL, 'http://dx.doi.org/10.1002/ecj.10112', '91', '2008', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'NakayamaObaAramotoEtAl2008', NULL, '1');
