INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005309', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'PENGSHONTAN2011', 'We examine whether XBRL adoption by publicly traded firms on the Shanghai Stock Exchange and Shenzhen Stock Exchange is related to the level of total accruals that firms report in the pre-XBRL versus post-XBRL periods. Our results indicate that the level of total accruals in the post-XBRL period is lower relative to the pre-XBRL period. This finding is robust to several controls for macroeconomic conditions and firm fundamentals. Moreover, we find this main effect is most prominent for firms that are most likely to benefit from greater transparency: high-growth firms, small firms, and firms in high-technology industries. One interpretation of our results is that XBRL implementation decreases investor’s information acquisition costs and thereby improves their ability to detect earnings management; managers in turn reduce accruals.', NULL, NULL, 'PENG, EMMA YAN and SHON, JOHN and TAN, CHRISTINE', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1111/j.1911-3838.2011.00021.x', NULL, NULL, NULL, NULL, NULL, NULL, 'Accounting Perspectives', NULL, NULL, 'XBRL, China, Accruals, Earnings quality', NULL, NULL, NULL, NULL, '2', NULL, '109--138', NULL, NULL, NULL, 'Blackwell Publishing Ltd', NULL, NULL, NULL, 'XBRL and Accruals: Empirical Evidence from China', NULL, 'http://dx.doi.org/10.1111/j.1911-3838.2011.00021.x', '10', '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'PENGSHONTAN2011', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005307', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'PENGJANIECHANG2010', 'The Church in Somewhere (CIS) is a small community church which uses an Excel spreadsheet to keep its financial records. The church administrator is considering moving from a spreadsheet accounting system to a relational database system that can easily be expanded to include more information in the future. In this paper we examine the transforming process in this hypothetical case by following a resource-event-agent (REA) modeling paradigm to create a database. We then link the REA model to financial reporting using Microsoft Access. In addition, using the financial report in the database, students prepare and validate an eXtensible Business Reporting Language (XBRL) document for CIS. Instead of applying the complex U.S. Generally Accepted Accounting Principles (GAAP) Taxonomies, Release 2009, the case uses a dedicated CIS Taxonomy to complete the mapping and tagging processes.', NULL, NULL, 'PENG, JACOB and JANIE CHANG, C.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1111/j.1911-3838.2010.00004.x', NULL, NULL, NULL, NULL, NULL, NULL, 'Accounting Perspectives', NULL, NULL, 'Instance document, REA modeling, Relatio