INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005241', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'PinskerWheeler2009', 'Businesses continually invest in new information technologies (IT) for improved reporting. EXtensible Business Reporting Language (XBRL) is an example of IT that allows financial statements to be transmitted and analysed electronically. The international business community has shown strong support for XBRL, with several countries mandating financial reporting using XBRL. However, even though organisations may be required to transmit XBRL-formatted financial statements, users of such statements would still need to choose whether or not they wish to analyse them with XBRL-enabled analysis tools. Earlier research indicates that the individual user's perceptions of IT tools are frequently negatively biased and that IT tools are commonly underused as a result. If this general finding were to be true for XBRL use, it could seriously affect XBRL's diffusion into the business community. The authors, both CPAs and accounting professors, investigate the relationship between XBRL use and perception by surveying 61 MBA students as proxies for nonprofessional investors. Results indicate that although perceptions of XBRL are generally positive, increased use of XBRL leads to more positive perceptions. Thus, organisations promoting the spread of XBRL should consider making access to XBRL analysis tools as easy and widely available as possible. Additionally, organisations that issue XBRL-formatted reports are perceived more positively than ones that do not issue such reports (a halo effect). © 2009 Palgrave Macmillan.', NULL, NULL, 'Pinsker, R. and Wheeler, P.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1057/jdg.2009.6', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Disclosure and Governance', NULL, NULL, NULL, NULL, NULL, NULL, 'cited By 16', '3', NULL, '241-261', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Nonprofessional investors' perceptions of the efficiency and effectiveness of XBRL-enabled financial statement analysis and of firms providing XBRL-formatted information', NULL, 'http://www.scopus.com/inward/record.url?eid=2-s2.0-67849122467&partnerID=40&md5=780ba94bba4a2feef05894ac7f0335cb', '6', '2009', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'PinskerWheeler2009', NULL, '1');
