INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00004970', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'PolacekGianettoKhashanahEtAl2012', 'Systems engineers have always been concerned with the problems surrounding complex systems. An evolutionary class of complex systems called Complex Adaptive Systems (CAS) is even more relevant today as we struggle to understand and cope with the often undesirable emergence effects of some of these systems, evidenced by recurring financial and sociopolitical crises. A key characteristic of CAS is a large, diverse, and sometimes unknown set of interactions both internally and across the system boundary. For the purpose of managing this complexity we present in this paper the concept that a principles orientation as compared to rules orientation of a complex system is an important and useful characteristic in need of further study. The US financial system is used as a CAS exemplar in support of this inquiry. ©2012 Wiley Periodicals, Inc. Syst Eng 15', NULL, NULL, 'Polacek, George A. and Gianetto, David A. and Khashanah, Khaldoun and Verma, Dinesh', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1002/sys.21213', NULL, NULL, NULL, NULL, NULL, NULL, 'Systems Engineering', NULL, NULL, 'financial system, CAS, IFRS, GAAP, principles, rules, complexity, SoS, leverage points, systems engineering', NULL, NULL, NULL, NULL, '4', NULL, '433--447', NULL, NULL, NULL, 'Wiley Subscription Services, Inc., A Wiley Company', NULL, NULL, NULL, 'On principles and rules in complex adaptive systems: A financial system case study', NULL, 'http://dx.doi.org/10.1002/sys.21213', '15', '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'PolacekGianettoKhashanahEtAl2012', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005023', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'PonteCarvajal-TrujilloEscobar-Rodriguez2015', 'Abstract This paper proposes a model for the formation of online purchase intention based on perceived value, trust and the antecedents of perceived security and privacy, using the model proposed by Ray, Ow, and Kim (2011) extended to cover third-party assurance seals and related constructs, and examines the influence on trust of consumers' perceived information quality, privacy, and security. A total of 451 individuals participated in an experiment. The partial least squares technique was applied to data collected via a questionnaire to test the proposed model. The results indicate that online purchase intention depends on perceived value and trust. The main predictors of perceived trust are perceived information quality and perceived security. Consumers' perceived security depends on vendor reputation, website investment, third-party assurance seals, understanding of third-party seals, privacy and security policies, familiarity with the website, Internet privacy concerns, and disposition towards third-party certification. ', NULL, NULL, 'Enrique Bonsón Ponte and Elena Carvajal-Trujillo and Tomás Escobar-Rodríguez', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.tourman.2014.10.009', NULL, NULL, NULL, NULL, NULL, NULL, 'Tourism Management ', NULL, NULL, 'E-commerce', NULL, NULL, NULL, NULL, NULL, NULL, '286 - 302', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Influence of trust and perceived value on the intention to purchase travel online: Integrating the effects of assurance on trust antecedents ', NULL, 'http://www.sciencedirect.com/science/article/pii/S026151771400209X', '47', '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'PonteCarvajal-TrujilloEscobar-Rodriguez2015', NULL, '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005031', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'PremurosoBhattacharya2008', 'On March 16, 2005, the \{SEC\} issued Final Rule 33-8529 encouraging registrants to voluntarily file tagged financial statement information on the \{EDGAR\} reporting System using \{XBRL\} format. In this paper, we examine whether early and voluntary filers of financial information in \{XBRL\} format demonstrate superior corporate governance and operating performance relative to their non-adopting peers. We investigate performance, market, and structure-related firm variables. Our results suggest that corporate governance is significantly and positively associated with a firm's decision to be an early and voluntary filer of financial information in \{XBRL\} format. At the same time, firm performance factors including liquidity and firm size are also associated with the early and voluntary \{XBRL\} filing decision. Our findings should be particularly interesting for the SEC, as it considers the corporate governance and firm-performance related associations between certain registrants' early and voluntary response and its call for XBRL-based filings. ', NULL, NULL, 'Ronald F. Premuroso and Somnath Bhattacharya', NULL, NULL, NULL, NULL, NULL, NULL,