INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005056', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'SandeepRavishankar2015', 'Abstract Impact sourcing – the practice of bringing digitally-enabled outsourcing jobs to marginalized individuals – is an important emerging social innovation in the outsourcing industry. The impact sourcing model of delivering Information Technology and Business Process Outsourcing (IT–BPO) services not only seeks to deliver business value for clients, but is also driven by an explicit social mission to help marginalized communities enjoy the benefits of globalization. This dual focus has led to the ambitious claim that social value creation can be integral to (and not always by-products of) innovative IT–BPO models. Given the relative newness of the impact sourcing business model there is scarce research about how impact sourcing companies emerge and the process through which entrepreneurs build and operate such companies. This paper draws on a qualitative study of seven Indian impact sourcing companies and develops a process model of the individual-level motivational triggers of impact sourcing entrepreneurship, the entrepreneurial actions underpinning different phases of venture creation and the positive institutional-level influences on impact sourcing. The paper argues that since deeply personalized values are central to the creation and development of impact sourcing companies, the business model may not be easy to replicate. The analysis highlights an intensive period of embedding and robust alliances with local partners as crucial for the scalability and sustainability of the impact sourcing business model. It also emphasizes the role of ‘social’ encoding and mimicry in determining the extent to which impact sourcing companies are able to retain their commitment to marginalized communities. ', NULL, NULL, 'M.S. Sandeep and M.N. Ravishankar', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.jsis.2015.09.002', NULL, NULL, NULL, NULL, NULL, NULL, 'The Journal of Strategic Information Systems ', NULL, NULL, 'Impact sourcing', NULL, NULL, NULL, NULL, NULL, NULL, ' - ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Social innovations in outsourcing: An empirical investigation of impact sourcing companies in India ', NULL, 'http://www.sciencedirect.com/science/article/pii/S0963868715000529', NULL, '2015', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'SandeepRavishankar2015', NULL, '1');
