INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005074', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ValentinettiRea2013', 'The systematic adoption of the eXtensible Business Reporting Language (XBRL) for financial reporting represents a great challenge. Worldwide, a large number of regulators are making an effort to promote the adoption of this standard to simplify and enhance the communication of financial information. This requires the definition of well-structured taxonomies that can standardize and accommodate the content of financial reports prepared by firms. This study aims to analyze the regulator-led adoption of XBRL for financial reporting. It examines the XBRL taxonomies used by Italian firms to reflect their financial reporting under rule-based Italian GAAP and principles-based International Financial Reporting Standards (IFRS). We compare the alignment of the Italian GAAP taxonomy and the IFRS taxonomy with Italian companies' financial statements and find two different levels of fit. The results offer useful insights for regulators and policy makers in prescribing or establishing appropriate taxonomies. We illustrate the potential impacts of the different taxonomies on the quality of financial reporting in terms of comparability and potential loss of information.', NULL, NULL, 'Valentinetti, Diego and Rea, Michele A.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '10.1111/1911-3838.12008', NULL, NULL, NULL, NULL, NULL, NULL, 'Accounting Perspectives', NULL, NULL, 'XBRL, Push, Taxonomy, Financial reporting in Italy', NULL, NULL, NULL, NULL, '3', NULL, '237--259', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'XBRL for Financial Reporting: Evidence on Italian GAAP versus IFRS', NULL, 'http://dx.doi.org/10.1111/1911-3838.12008', '12', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'ValentinettiRea2013', NULL, '1');
