INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005157', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'WorrellGangiBush2013', 'Recent focus on the diversity of research methodologies available to accounting information systems (AIS) scholars has led researchers to suggest the Delphi method has reached the limits of its usefulness. Using a review of the accounting and information systems literature, we suggest such a finding is premature for the \{AIS\} discipline. The Delphi method is especially useful in reducing ambiguity through the use of expert panels of both practitioners and experts and informing relevant and timely issues facing organizations. In essence, the Delphi method has potential to provide both rigor and relevance to \{AIS\} researchers. Our purpose is to review the prior literature on the use of the Delphi method and discuss potential areas of research within the \{AIS\} discipline where the method might add value. Based on this review, we develop a series of guidelines on how to properly develop, administer, and assess panel responses and then use an illustrative study example that explores \{IT\} risks in operations. We conclude with a discussion of the value of the Delphi method and provide insight into its limitations. ', NULL, NULL, 'James L. Worrell and Paul M. Di Gangi and Ashley A. Bush', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'http://dx.doi.org/10.1016/j.accinf.2012.03.003', NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Accounting Information Systems ', NULL, NULL, 'Delphi method', NULL, NULL, NULL, NULL, '3', NULL, '193 - 208', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Exploring the use of the Delphi method in accounting information systems research ', NULL, 'http://www.sciencedirect.com/science/article/pii/S1467089512000188', '14', '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'WorrellGangiBush2013', NULL, '1');
