INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, ctlalt_stretch_factor, ctldash_repeated_names, ctlmax_names_forced_etal, ctlnames_show_etal, ctluse_alt_spacing, ctluse_article_number, ctluse_paper, ctluse_url, ctluseforced_etal, doi, edition, editor, eid, file, howpublished, institution, journal, journaltitle, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, ps, publisher, school, search, series, title, type, url, volume, year, date, __groupsearch, priority, relevance, printed, qualityassured, readstatus, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, ranking, database_id) VALUES ('00005283', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'ZhaoYenChang2004a', 'Financial statements are not as important to investors as they once were, as technology has changed the way companies create value today. While these changes pose serious threats to the economic viability of auditing, they also create new opportunities for auditors to pursue. Both the American Institute of Certified of Public Accountants and the Canadian Institute of Chartered Accountants (CICA) Task Force on Assurance Services have identified continuous auditing as a service that should be offered. Continuous auditing is significantly different from an annual financial statement audit. A latest research report produced by the CICA defines a continuous audit as: "a methodology that enables independent auditors to provide written assurance on a subject matter using a series of auditors' reports issued simultaneously with, or a short period of time after, the occurrence of events underlying the subject matter." However, continuous auditing would present significant technical hurdles. These technical hurdles could be overcome if certain conditions exist. Computer-assisted audit tools (CAATs) are one of the conditions that must exist in order to conduct the continuous auditing. CAATs are defined as computer-assisted tools that permit auditors to increase their productivity, as well as that of the audit function. Therefore, with the real-time accounting and electronic data interchange popularizing, CAATs are becoming even more necessary. The demand for timely and forward-looking information hints that the continuous audit will eventually replace the traditional audit report on year-end results. &copy; Emerald Group Publishing Limited.', NULL, NULL, 'Zhao, Ning and Yen, David C. and Chang, I-Chiu', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Information Management and Computer Security', NULL, 'Financial data processing', 'Artificial intelligence;Computer programming languages;Electronic commerce;Electronic data interchange;Expert systems;Information technology;Real time systems;Security systems;', 'English', NULL, NULL, 'Computer assisted audit tools (CAATs);Continuous auditing;Economic viability;Extensive business reporting language (XBRL);Financial statements;Real time accounting (RTA);', '5', NULL, '389 - 400', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Auditing in the e-commerce era', NULL, 'http://dx.doi.org/10.1108/09685220410563360', '12', '2004', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[vagner:6]', 'ZhaoYenChang2004a', NULL, '1');
